# meta-data.lib.civet

import {
	undef, defined, notdefined, isString, assert, croak,
	hash, hashof,
	} from 'datatypes'
import {f, fromTAML} from 'llutils'

export type TConverter = (str: string) => unknown

hMetaDataTypes: hashof<TConverter> := {
	'---': (block: string) =>
		return fromTAML("---\n#{block}")
	}

# ---------------------------------------------------------------------------

export addMetaDataType := (
	marker: string,
	converter: TConverter
	): void =>

	assert (marker.length == 3), f"Bad 'start' key: #{marker}"
	assert (marker[1] == marker[0]) && (marker[2] == marker[0]),
		"Bad 'start' key: #{marker}"

	hMetaDataTypes[marker] = converter
	return

# ---------------------------------------------------------------------------

export isMetaDataStart := (str: string): boolean =>

	return (str in hMetaDataTypes)

# ---------------------------------------------------------------------------
# --- block does NOT contain the meta data start line

export convertMetaData := (
		marker: string,
		block: string
		): unknown =>

	assert isMetaDataStart(marker), "Bad meta data"
	converter := hMetaDataTypes[marker]
	return converter(block)

# section-map.lib.civet

import {
	undef, defined, notdefined, assert, croak, hash, hashof,
	TStringMapper, assertIsHash,
	isEmpty, nonEmpty, isString, isHash, isArray, isFunction,
	} from 'datatypes'
import {
	f, toBlock, isTAML, fromTAML, hasKey, hasKeys,
	} from 'llutils'
import {OL, ML} from 'to-nice'
import {isSectionName, Section} from 'section'

# ---------------------------------------------------------------------------

export isSetName := (name: string): boolean =>

	return defined(name.match(/^[A-Z][A-Za-z0-9_-]*/))

# ---------------------------------------------------------------------------
# --- NOTE: section names begin with a lower case letter
#           set names begin with an upper case letter
# --- tree is a tree of section/set names
# --- hMappers are callbacks that are called
#        when a set or section is processed
#        should be <name> -> <function>
#     <name> can be a section name or a set name
#     <function> should be <block> -> <block>

export type TSectionTree = {
	name: string
	mapper?: TStringMapper
	level?: number
	lChildren: (string | Section | TSectionTree)[]
	}

# ---------------------------------------------------------------------------

export isSectionTree := (h: unknown): h is TSectionTree =>

	if isHash(h)
		if not hasKeys(h, 'name', 'mapper', 'lChildren')
			return false
		{name, mapper, lChildren} := h
		if not isString(name) || not isSetName(name)
			return false
		if defined(mapper) && not isFunction(mapper)
			return false
		if not isArray(lChildren)
			return false
		for item of lChildren
			if (not isString(item)
					&& (item not instanceof Section)
					&& not isSectionTree(item)
					)
				return false
		return true
	else
		return false

# ---------------------------------------------------------------------------

emptyTree: TSectionTree := {
	name: ''
	mapper: undef
	lChildren: []
	}

# ---------------------------------------------------------------------------

export nonEmptyFilter := (x: unknown) =>

	return nonEmpty(x)

# ---------------------------------------------------------------------------

export class SectionMap

	hTree: TSectionTree = emptyTree

	# --- These index parts of @hTree
	hSections: hashof<Section>       = {}
	hSets:     hashof<TSectionTree>  = {}
	hMappers:  hashof<TStringMapper> = {}

	constructor(
			tree: string | TSectionTree,
			@hMappers: hashof<TStringMapper> = {}
			)

		if isString(tree)
			if isTAML(tree)
				x := fromTAML(tree)
				if isSectionTree(x)
					@hTree = x
				else
					croak "Bad Section Tree: #{tree}"
			else
				croak "Bad Section Tree: #{tree}"
		else
			@hTree = tree
		@analyze(@hTree)

	# ..........................................................
	# --- converts all children that are strings
	#     into true Section objects

	analyze(hTree: TSectionTree): void

		{name: rootName, lChildren} := hTree
		@hSets[rootName] = hTree

		for child,i of lChildren
			if isString(child)
				# --- convert to a Section object
				sec := new Section(child)
				@hSections[child] = lChildren[i] = sec
				if (child in @hMappers)
					sec.mapper = @hMappers[child]
			else if (child instanceof Section)
				{name, mapper} := child
				@hSections[name] = child
				if defined(mapper)
					@hMappers[name] = mapper
				else if (name in @hMappers)
					child.mapper = @hMappers[name]
			else                # --- (child instanceof TSectionTree)
				@analyze(child)

	# ..........................................................

	add(name: string, ...lLines: string[]): void

		assert isSectionName(name), "Bad section name: #{name}"
		assert (name in @hSections), "No such section: #{name}"
		section := @hSections[name]
		section.add ...lLines
		return

	# ..........................................................

	getTreeBlock(tree: TSectionTree): string

		{name, mapper, level, lChildren} := tree
		lParts := for child of lChildren
			if isString(child)
				croak "child is a string"
			else if (child instanceof Section)
				child.getBlock()
			else
				@getTreeBlock(child)
		return lParts.filter(nonEmptyFilter).join('\n')

	# ..........................................................

	getBlock(name: string? = undef): string?

		if defined(name)
			if isSectionName(name)
				assert (name in @hSections), "No such section: #{name}"
				section := @hSections[name]
				return section.getBlock()
			else if isSetName(name)
				assert (name in @hSets), "No such set: #{name}"
				return @getTreeBlock @hSets[name]
		else
			return @getTreeBlock @hTree

# section.lib.civet

import {
	undef, defined, assert, croak, TStringMapper,
	isArray, isEmpty, isFunction, isInteger,
	} from 'datatypes'
import {toBlock} from 'llutils'
import {OL} from 'to-nice'
import {indented} from 'indent'

# ---------------------------------------------------------------------------

export isSectionName := (name: string): boolean =>

	return defined(name.match(/^[a-z][A-Za-z0-9_-]*/))

# ---------------------------------------------------------------------------

export class Section

	name: string
	mapper?: TStringMapper
	level?: number

	lLines: string[] = []

	constructor(
			@name: string,
			level: number? = undef
			mapper: TStringMapper? = undef)

		assert isSectionName(@name), "Bad Section Name: #{@name}"
		if defined(mapper)
			assert isFunction(mapper),
					"bad mapper in section #{OL(@name)}"
			@mapper = mapper
		if defined(level)
			@level = level

	# ..........................................................

	add(...lLines: string[]): void

		if isInteger(lLines[0])
			level := lLines[0]
			for line,i of lLines
				if (i > 0)
					@lLines.push indented(line, level)
		else
			for line of lLines
				@lLines.push line
		return

	# ..........................................................

	prepend(...lLines: string[])

		if isInteger(lLines[0])
			level := lLines[0]
			for line,i of lLines.toReversed()
				if (i < lLines.length-1)
					@lLines.unshift indented(line, level)
		else
			for line of lLines.toReversed()
				@lLines.unshift line
		return

	# ..........................................................

	getBlock(): string?

		if (@lLines.length == 0)
			return undef
		block := toBlock(@lLines)
		mapped := defined(@mapper) ? @mapper(block) : block
		return @level ? indented(mapped, @level) : mapped

# walker.lib.civet

import {
	undef, defined, notdefined, integer, hash, array,
	isArray, isHash, isPrimitive, assert, croak, isEmpty,
	assertIsString, assertIsArray,
	} from 'datatypes'
import {OL} from 'to-nice'
import {DBG} from 'logger'

# ---------------------------------------------------------------------------

hasChildren := (x: unknown) =>
	return isHash(x) || isArray(x)

export type TVisitKind = 'enter' | 'exit' | 'ref'

export class Walker<T extends object = hash>

	# --- Keep track of nodes visited to avoid infinite loops
	setVisited: WeakSet<hash | array> = new WeakSet<hash | array>()
	lNodeStack: T[] = []

	# ..........................................................

	protected pushNode(item: T): void

		@lNodeStack.push item
		return

	# ..........................................................

	protected popNode(): T?

		return @lNodeStack.pop()

	# ..........................................................

	get level(): integer

		return @lNodeStack.length

	# ..........................................................

	parent(n: integer = 1): T

		len := @lNodeStack.length
		assert (n >= 0) && (n < len), "Bad index: #{n} of #{len}"
		return @lNodeStack[len - n]

	# ..........................................................

	isNode(x: unknown): x is T

		return true

	# ..........................................................

	filter(node: T): boolean

		return true

	# ..........................................................
	# GENERATOR

	walk(x: unknown): Generator<T, void, void>

		if hasChildren(x)
			@setVisited = new WeakSet<hash | array>()
			yield *@walkItem(x)
		return

	# ..........................................................
	# GENERATOR

	walkItem(item: hash | array): Generator<T, void, void>

		if @setVisited.has(item)
			return
		if @isNode(item)
			# --- type narrowing ensures that item is a T
			if @filter(item)
				DBG "YIELD NODE: #{OL(item)}"
				yield item
			# --- item is parent of yielded items
			@pushNode item
		@setVisited.add item
		if isArray(item)
			let i1 = 0
			for x of item
				i := i1++
				if isHash(x) || isArray(x)
					yield *@walkItem(x)
		else
			for [key, x] of Object.entries(item)
				if isHash(x) || isArray(x)
					yield *@walkItem(x)
		if @isNode(item)
			@popNode()
		return

	# ..........................................................
	# GENERATOR

	walkEx(x: unknown): Generator<[TVisitKind, T], void, void>

		if hasChildren(x)
			@setVisited = new WeakSet<hash | array>()
			yield *@walkItemEx(x)
		return

	# ..........................................................
	# GENERATOR

	walkItemEx(item: hash | array): Generator<[TVisitKind, T], void, void>

		if @setVisited.has(item)
			if @isNode(item)
				yield ['ref', item]
			return
		if @isNode(item)
			# --- type narrowing ensures that item is a T
			if @filter(item)
				DBG "YIELD NODE: #{OL(item)}"
				yield ['enter', item]
			# --- item is parent of yielded items
			@pushNode item
		@setVisited.add item
		if isArray(item)
			let i2 = 0
			for x of item
				i := i2++
				if isHash(x) || isArray(x)
					yield *@walkItemEx(x)
		else
			for [key, x] of Object.entries(item)
				if isHash(x) || isArray(x)
					yield *@walkItemEx(x)
		if @isNode(item)
			yield ['exit', item]
			@popNode()
		return
